import React from "react";
import { VariantProps, CSS } from "../stitches.config";
import { StyledIcon } from "./Icon.styled";
/**
 * Define SVG subelement <title>
 */
declare type TitleShape = {
    children: React.ReactChild;
};
/**
 * Define <svg>
 */
declare type IconShape = {
    children: React.ReactChild | React.ReactChild[];
};
interface IconComposition {
    Add: React.FC<any>;
    Audio: React.FC;
    Close: React.FC;
    Image: React.FC;
    Title: React.FC<TitleShape>;
    Video: React.FC;
}
declare type VariantComponentProps = React.SVGAttributes<SVGElement>;
declare type IconVariants = VariantProps<typeof StyledIcon>;
declare type IconProps = VariantComponentProps & IconVariants & {
    css?: CSS;
} & IconShape;
declare const Icon: React.FC<IconProps> & IconComposition;
export { Icon };
