import React from "react";
import { CSS, VariantProps } from "../stitches.config";
import { StyledContent } from "./Popover.styled";
/**
 *
 */
declare type TriggerShape = {
    children: React.ReactChild;
    "aria-controls"?: string;
};
/**
 *
 */
declare type ContentShape = {
    children: React.ReactChild | React.ReactChild[];
    id?: string;
};
declare type ContentVariants = VariantProps<typeof StyledContent>;
/**
 *
 */
declare type PopoverShape = {
    children: React.ReactChild | React.ReactChild[];
};
interface PopoverComposition {
    Content: React.FC<ContentShape & ContentVariants>;
    Trigger: React.FC<TriggerShape>;
}
declare type PopoverProps = PopoverShape & {
    css?: CSS;
};
declare const Popover: React.FC<PopoverProps> & PopoverComposition;
export { Popover };
