/// <reference types="react" />
declare type PropShape = {
    name: string;
    type: string;
    description: string | React.ReactChild;
};
declare type TableComponentProps = React.TableHTMLAttributes<HTMLTableElement>;
declare type TableProps = TableComponentProps & {
    items: Array<PropShape>;
};
declare const PropsTable: (props: TableProps) => JSX.Element;
export default PropsTable;
