	<script>
		function dynvar() {
			var vars = {};
			var x = document.location.search.substring(1).split('&');
	
			for (var i in x) {
				var z = x[i].split('=',2);
	
				if (!vars[z[0]]) {
					vars[z[0]] = unescape(z[1]);
				}
			}
	
			document.getElementById('pagingText').innerHTML = 'page ' + vars.page; // + ' of ' + vars.topage
		}
  		
	</script>
	</div>
